package main

import "fmt"
import "strconv"

func main() {
	// added the first digit (3) on to the end for the looping effect
	const input = ""

	sum := 0;

	for i := 0; i < len(input)-1; i++ {
		curr := string(input[i]);
		next := string(input[i+1]);
		if (next == curr) {
			value, _ := strconv.Atoi(next)
			sum += value
		}
	}

	fmt.Println(sum);
}
